package  com.zhou.core.gm.cs.core.client.console.filter.codec;import java.util.HashMap;import java.util.Map;import org.apache.mina.core.session.IoSession;import com.zhou.core.gm.cs.buffer.GmIoBuffer;	/**	 * 请求类型抽象数据	 * @author zhouyongjun	 *	 */public abstract class ClientAbstractGmResp{	public static Map<Integer,ClientAbstractGmResp> registers = new HashMap<Integer, ClientAbstractGmResp>();	public static final byte RESULT_SUCC = 0;	public static final byte RESULT_FAIL = 1;	public ClientAbstractGmResp () {	}		public abstract int getProtocolId();	public abstract void handle(GmIoBuffer in,IoSession session,byte result,String restultMsg);		public static void register(ClientAbstractGmResp resp) {		registers.put(resp.getProtocolId(), resp);	}		public static ClientAbstractGmResp getResponse(int pro_id) {		if (registers.containsKey(pro_id)) {			return registers.get(pro_id);			}		return null;	}	public static Map<Integer, ClientAbstractGmResp> getRegisters() {		return registers;	}}