package com.zhou.core.gm.cs.core.client.console.filter.codec;import org.apache.mina.core.session.IoSession;import org.apache.mina.filter.codec.ProtocolCodecFactory;import org.apache.mina.filter.codec.ProtocolDecoder;import org.apache.mina.filter.codec.ProtocolEncoder;	/**	 * 数据处理类	 * @author zhouyongjun	 *	 */public class ClientCodecFactory implements ProtocolCodecFactory {	ClientRequestEncoder encoder;	ClientResponseDecoder dencoder;	public ClientCodecFactory() {		encoder = new ClientRequestEncoder();		dencoder = new ClientResponseDecoder();	}	@Override	public ProtocolDecoder getDecoder(IoSession session) throws Exception {		return dencoder;	}	@Override	public ProtocolEncoder getEncoder(IoSession session) throws Exception {		return encoder;	}}