package com.zhou.core.gm.cs.core.server.console.filter.codec;import org.apache.mina.core.session.IoSession;import org.apache.mina.filter.codec.ProtocolCodecFactory;import org.apache.mina.filter.codec.ProtocolDecoder;import org.apache.mina.filter.codec.ProtocolEncoder;	/**	 * 数据处理类	 * @author zhouyongjun	 *	 */public class CodecFactory implements ProtocolCodecFactory {	ResponseEncoder encoder;	RequestDecoder dencoder;	public CodecFactory() {		encoder = new ResponseEncoder();		dencoder = new RequestDecoder();	}	@Override	public ProtocolDecoder getDecoder(IoSession session) throws Exception {		return dencoder;	}	@Override	public ProtocolEncoder getEncoder(IoSession session) throws Exception {		return encoder;	}}