package  com.zhou.core.gm.cs.core.server.console.filter.codec;import org.apache.mina.core.buffer.IoBuffer;import org.apache.mina.core.session.IoSession;import org.apache.mina.filter.codec.ProtocolEncoderAdapter;import org.apache.mina.filter.codec.ProtocolEncoderOutput;import com.zhou.core.gm.cs.log.GmLog;	/**	 * 编码器 用于服务器数据下发	 * @author zhouyongjun	 *	 */public class ResponseEncoder extends ProtocolEncoderAdapter {	@Override	public void encode(IoSession session, Object message, ProtocolEncoderOutput output) throws Exception {		IoBuffer buffer;		try {			AbstractGmResp response = (AbstractGmResp) message;			response.encode();			buffer = response.toClient();			session.write(buffer);			GmLog.info("GM response[0x" + Integer.toHexString(response.getProtocolId())+"] send to session["+session+"] limit:"+buffer.limit());		} catch (Exception e) {			e.printStackTrace();		}	}}